import React from 'react';
impo
const LanguageSelection = ({ onLanguageChange }) => {
    const handleLanguageChange = (language) => {
        onLanguageChange(language); // Send the selected language to the parent component
    };

    return (
        <div>
            <label>Select Language:</label>
            <div style={styles.buttonContainer}>
                <button style={styles.button} onClick={() => handleLanguageChange('Tamil')}>
                    Tamil
                </button>
                <button style={styles.button} onClick={() => handleLanguageChange('English')}>
                    English
                </button>
            </div>
        </div>
    );
};

// Styles for buttons and container
const styles = {
    buttonContainer: {
        display: 'flex',
        gap: '10px', // Space between buttons
        marginTop: '5px', // Margin above buttons
    },
    button: {
        padding: '10px 20px',
        fontSize: '16px',
        color: 'white',
        backgroundColor: '#0056b3',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        transition: 'background-color 0.3s',
    },
};

// Adding hover effect
styles.buttonHover = {
    backgroundColor: '#004494', // Darker shade for hover effect
};

export default LanguageSelection;
