import React, { useState } from 'react';

const App = () => {
    const [input, setInput] = useState('');
    const [message, setMessage] = useState('');

    const sendMessage = () => {
        const trimmedInput = input.trim();
        if (trimmedInput) {
            fetch(`http://localhost:70/fetch-kural?query=${encodeURIComponent(trimmedInput)}`)
                .then(response => response.json())
                .then(data => {
                    if (data._id) {
                        setMessage(
                            `<div class="message bot-message">
                  <strong>Chapter Name:</strong> ${data.ChapterName}<br>
                  <strong>Section Name:</strong> ${data.SectionName}<br>
                  <strong>Verse:</strong> ${data.Verse}<br>
                  <strong>Translation:</strong> ${data.Translation}
              </div>`
                        );
                    } else {
                        setMessage(
                            `<div class="message bot-message">${data.response}</div>`
                        );
                    }
                })
                .catch(error => {
                    console.error('Error:', error);
                    setMessage(
                        `<div class="message bot-message">An error occurred. Please try again.</div>`
                    );
                });
        } else {
            console.log('Input is empty');
        }
    };

    return (
        <div>
            <h1>Kural Fetcher</h1>
            <input
                id="input"
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
            />
            <button onClick={sendMessage}>Send</button>
            <div id="message-section" dangerouslySetInnerHTML={{ __html: message }} />
        </div>
    );
};

export default App;
