// src/components/KuralSearch.js
import React, { useState } from 'react';
import axios from 'axios';
import styles from './KuralSearch.module.css';

const KuralSearch = () => {
    const [selectedField, setSelectedField] = useState(''); // Selected search field
    const [inputValue, setInputValue] = useState(''); // Input value for search
    const [results, setResults] = useState([]); // Search results
    const [error, setError] = useState(''); // Error message

    // Handle the search action when the button is clicked
    const handleSearch = async () => {
        if (!selectedField || !inputValue.trim()) {
            setError('Please select a field and provide a valid input');
            return;
        }

        try {
            const response = await axios.get('http://localhost:5000/api/kurals', {
                params: {
                    [selectedField]: inputValue.trim()
                }
            });

            setResults(response.data); // Update results with fetched data
            setError(''); // Clear any previous errors
        } catch (err) {
            setError('Error fetching data'); // Set error state if something goes wrong
            setResults([]); // Clear results in case of error
        }
    };

    return (
        <div className={styles.container}>
            <h1>Search Thirukkural</h1>

            {/* Field Selection Dropdown */}
            <div>
                <label>Select Field:</label>
                <select value={selectedField} onChange={(e) => setSelectedField(e.target.value)}>
                    <option value="">Select a field</option>
                    <option value="chapterName">Chapter Name</option>
                    <option value="sectionName">Section Name</option>
                    <option value="verse">Verse</option>
                </select>
            </div>

            {/* Input Field for Query */}
            {selectedField && (
                <div>
                    <label>
                        {selectedField.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}:
                    </label>
                    <input
                        type={selectedField === 'verse' ? 'number' : 'text'}
                        value={inputValue}
                        onChange={(e) => setInputValue(e.target.value)}
                    />
                </div>
            )}

            {/* Search Button */}
            <button onClick={handleSearch}>Search</button>

            {/* Error Message Display */}
            {error && <p className={styles.error}>{error}</p>}

            {/* Results List */}
            <ul>
                {results.map((kural, index) => (
                    <li key={index}>
                        <p>Chapter Name: {kural.chapterName}</p>
                        <p>Section Name: {kural.sectionName}</p>
                        <p>Verse: {kural.verse}</p>
                        <p>{kural.translation}</p>
                        <p>{kural.explanation}</p>
                        <p>Number: {kural.number}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default KuralSearch;
