import React, { useState } from 'react';
import axios from 'axios';
import styles from './KuralSearch.module.css';

const KuralSearch = () => {
    const [selectedField, setSelectedField] = useState('');
    const [inputValue, setInputValue] = useState('');
    const [results, setResults] = useState([]);
    const [error, setError] = useState('');

    const isTamil = (text) => /[\u0B80-\u0BFF]/.test(text);  // Check for Tamil characters

    const isPhoneticEnglish = (text) => {
        const commonPhoneticPatterns = [
            // (Same list as before)
        ];
        return commonPhoneticPatterns.some(pattern => text.includes(pattern));
    };

    const isEnglishWord = async (word) => {
        try {
            const response = await axios.get('https://api.dictionaryapi.dev/api/v2/entries/en/' + word);
            return response.data && response.data.length > 0 && response.data[0].word.toLowerCase() === word.toLowerCase();
        } catch (error) {
            return error.response?.status === 404 ? false : true; // 404 means word not found
        }
    };

    const classifyInput = async (text) => {
        if (isTamil(text)) {
            return "Tamil";
        } else if (await isEnglishWord(text)) {
            return "English";
        } else if (isPhoneticEnglish(text)) {
            return "Phonetic";
        }
        return "Unknown";
    };

    const handleInput = (field) => {
        setSelectedField(field);
        setInputValue('');
    };

    const handleSearch = async () => {
        let fieldName;
        let apiUrl;

        const inputType = await classifyInput(inputValue);

        if (selectedField === 'inputs' && inputType === 'Tamil') {
            apiUrl = 'http://localhost:5000/api/questions';
            fieldName = 'inputs';
        } else if (selectedField === 'inputs' && inputType === 'English') {
            apiUrl = 'http://localhost:5000/api/questions';
            fieldName = 'english_input';
        } else {
            fieldName = (() => {
                if (selectedField === 'chapterName' && classifyInput(inputValue) === 'English') {
                    return 'Chapter_Eng';
                } 
                if (selectedField === 'chapterName' && classifyInput(inputValue) === 'Phonetic') {
                    return 'Chapter';
                }
                if (selectedField === 'sectionName' && classifyInput(inputValue) === 'English') {
                    return 'section_eng';
                }
                if (selectedField === 'sectionName' && classifyInput(inputValue) === 'Phonetic') {
                    return 'section_trans';
                }
                if (selectedField === 'verse' && classifyInput(inputValue) === 'English') {
                    return 'translation';
                }
                if (selectedField === 'verse' && classifyInput(inputValue) === 'Phonetic') {
                    return 'verse';
                }
                
                return selectedField;            })();

            apiUrl = 'http://localhost:5000/api/kurals';
        }

        try {
            const response = await axios.get(apiUrl, { params: { [fieldName]: inputValue } });
            setResults(response.data);
            setError('');
        } catch (err) {
            setError('Error fetching data. Please try again.');
            setResults([]);
        }
    };

    return (
        <div className={styles.container}>
            <h1>Search Thirukkural</h1>
            <div>
                <label>Select Field:</label>
                <div className={styles.optionsContainer}>
                    <div className={styles.optionBox} onClick={() => handleInput('chapterName')}>Chapter Name</div>
                    <div className={styles.optionBox} onClick={() => handleInput('sectionName')}>Section Name</div>
                    <div className={styles.optionBox} onClick={() => handleInput('verse')}>Verse</div>
                    <div className={styles.optionBox} onClick={() => handleInput('inputs')}>Other Questions</div>
                </div>
            </div>

            {selectedField && (
                <div>
                    <label>{selectedField.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}:</label>
                    <input 
                        type="text" 
                        value={inputValue} 
                        onChange={(e) => setInputValue(e.target.value)} 
                    />
                </div>
            )}
            
            <button onClick={handleSearch}>Search</button>

            {error && <p className={styles.error}>{error}</p>}

            <ul>
                {results.length > 0 ? results.map((kural, index) => (
                    <li key={index}>
                         {selectedField === 'inputs' ? (
                            <p>Answer: {kural.targets}</p>
                        ) : (
                            <>
                                <p>Chapter Name: {kural.chapterName}</p>
                                <p>Chapter Group: {kural.chapter_group_tam}</p>
                                <p>Section Name: {kural.sectionName}</p>
                                <p>Verse: {kural.verse}</p>
                                <p>Translation: {kural.translation || 'Not available'}</p>
                                <p>Explanation: {kural.explanation || 'Not available'}</p>
                                <p>Number: {kural.number}</p>
                            </>
                        )}
                    </li>
                )) : <p>No results found.</p>}
            </ul>
        </div>
    );
};

export default KuralSearch;
