import React from 'react';
import './language.module.css';
const LanguageSelection = ({ onLanguageChange }) => {
    const handleLanguageChange = (language) => {
        onLanguageChange(language); // Send the selected language to the parent component
    };

    return (
        <div class="c1">
            <label>Select Language:</label>
            <div class="buttonContainer">
                <button class=".button" onClick={() => handleLanguageChange('Tamil')}>
                    Tamil
                </button>
                <button class=".button" onClick={() => handleLanguageChange('English')}>
                    English
                </button>
            </div>
        </div>
    );
};



export default LanguageSelection;
