import React, { useState } from 'react';
import axios from 'axios';
import styles from './KuralSearch.module.css';

const KuralSearch = () => {
    const [selectedField, setSelectedField] = useState('');
    const [inputValue, setInputValue] = useState('');
    const [results, setResults] = useState([]);
    const [error, setError] = useState('');

    const isTamil = (text) => /[\u0B80-\u0BFF]/.test(text);  // Check for Tamil characters

    const isEnglishWord = async (sentence) => {
        const words = sentence.split(' '); // Split sentence into words
        let validWordCount = 0;
        for (const word of words) {
            try {
                const response = await axios.get(`https://api.dictionaryapi.dev/api/v2/entries/en/${word}`);
                if (response.data && response.data.length > 0 && response.data[0].word.toLowerCase() === word.toLowerCase()) {
                    validWordCount++; // Increment valid word count if it's an English word
                }
            } catch (error) {
                if (error.response && error.response.status === 404) {
                    continue; // Skip if the word is not found in the dictionary
                } else {
                    console.error(`Error checking word "${word}":`, error);
                }
            }
        }

        // If more than 50% of words are valid English words, consider it English
        return validWordCount >= words.length / 2;
    };

    const classifyInput = async(text) => {
        if (isTamil(text)) {
            return "Tamil";
        }
        const englishCheckResult = await isEnglishWord(text);
        if (englishCheckResult === true) {
            return "English";
        } else {
            return "Phonetic";
        }
    };

    const handleInput = (field) => {
        setSelectedField(field); // Set the selected field when a box is clicked
        setInputValue(''); // Clear previous input if any
    };

    const handleSearch = async () => {
        let fieldName;
        let apiUrl;

        const classification = await classifyInput(inputValue);
        if (selectedField === 'inputs' && classification === 'Tamil') {
            apiUrl = 'http://localhost:5000/api/questions';
            fieldName = 'inputs';
        } else if (selectedField === 'inputs' && classification === 'English') {
            apiUrl = 'http://localhost:5000/api/questions';
            fieldName = 'english_input';
        } else {
            fieldName = (() => {
                if (selectedField === 'chapterName' && classification === 'English') {
                    return 'Chapter_Eng';
                } 
                if (selectedField === 'chapterName' && classification === 'Phonetic') {
                    return 'Chapter';
                }
                if (selectedField === 'sectionName' && classification === 'English') {
                    return 'section_eng';
                }
                if (selectedField === 'sectionName' && classification === 'Phonetic') {
                    return 'section_trans';
                }
                if (selectedField === 'verse' && classification === 'English') {
                    return 'translation';
                }
                if (selectedField === 'verse' && classification === 'Phonetic') {
                    return 'verse';
                }
                
                return selectedField;
            })();

            apiUrl = 'http://localhost:5000/api/kurals'; 
        }

        try {
            const response = await axios.get(apiUrl, { params: { [fieldName]: inputValue } });
            setResults(response.data);
            setError('');
        } catch (err) {
            console.error('Error fetching data:', err);
            setError('Error fetching data');
            setResults([]);
        }
    };

    return (
        <div className={styles.container}>
            <h1>Search Thirukkural</h1>
            
            <div>
                <label>Select Field:</label>
                <div className={styles.optionsContainer}>
                    <div className={styles.optionBox} onClick={() => handleInput('chapterName')}>Chapter Name</div>
                    <div className={styles.optionBox} onClick={() => handleInput('sectionName')}>Section Name</div>
                    <div className={styles.optionBox} onClick={() => handleInput('verse')}>Verse</div>
                    <div className={styles.optionBox} onClick={() => handleInput('inputs')}>Other Questions</div>
                    <div className={styles.optionBox} onClick={() => handleInput('fullDetails')}>Full Details</div> {/* New Full Details option */}
                </div>
            </div>

            {selectedField && (
                <div>
                    <label>{selectedField.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}:</label>
                    <input 
                        type="text" 
                        value={inputValue} 
                        onChange={(e) => setInputValue(e.target.value)} 
                    />
                </div>
            )}
            
            <button onClick={handleSearch}>Search</button>

            {error && <p className={styles.error}>{error}</p>}

            <ul>
                {results.length > 0 ? results.map((kural, index) => (
                    <li key={index}>
                        {selectedField === 'inputs' ? (
                            <>
                                <p>Answer: {kural.targets}</p>
                                <p>Number: {kural.number}</p>
                            </>
                        ) : selectedField === 'fullDetails' ? ( // Render full details if the option is selected
                            <>
                                <p>Chapter Name: {kural.chapterName}</p>
                                <p>Chapter Group: {kural.chapter_group_tam}</p>
                                <p>Section Name: {kural.sectionName}</p>
                                <p>Verse: {kural.verse}</p>
                                <p>Translation: {kural.translation || 'Not available'}</p>
                                <p>Explanation: {kural.explanation || 'Not available'}</p>
                                <p>Number: {kural.number}</p>
                            </>
                        ) : (
                            <>
                                <p>Chapter Name: {kural.chapterName}</p>
                                <p>Chapter Group: {kural.chapter_group_tam}</p>
                                <p>Section Name: {kural.sectionName}</p>
                                <p>Verse: {kural.verse}</p>
                                <p>{kural.translation || 'Not available'}</p>
                                <p>{kural.explanation || 'Not available'}</p>
                                <p>Number: {kural.number}</p>
                            </>
                        )}
                    </li>
                )) : <p>No results found.</p>}
            </ul>
        </div>
    );
};

export default KuralSearch;
