import React from 'react';
import './language.module.css'; // Import a CSS file for additional styles

const LanguageSelection = ({ onLanguageChange }) => {
    const handleLanguageChange = (language) => {
        onLanguageChange(language); // Send the selected language to the parent component
    };

    return (
        <div className="language-selection-container">
            <div className="header">
                <h2>Select Your Language</h2>
            </div>
            <div className="button-container">
                <button className="language-button" onClick={() => handleLanguageChange('Tamil')}>
                    Tamil
                </button>
                <button className="language-button" onClick={() => handleLanguageChange('English')}>
                    English
                </button>
            </div>
        </div>
    );
};

export default LanguageSelection;
