// src/LanguageSelection.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './language.module.css';

const LanguageSelection = () => {
    const [selectedLanguage, setSelectedLanguage] = useState('');
    const navigate = useNavigate();

    const handleLanguageSelect = (language) => {
        setSelectedLanguage(language);
        navigate('/search', { state: { language } }); // Navigate to search page with the selected language
    };

    return (
        <div className={styles.languageContainer}>
            <h1>Welcome to KuralBot</h1>
            <p>Please select your language:</p>
            <div className={styles.languageOptions}>
                <button onClick={() => handleLanguageSelect('Tamil')}>Tamil</button>
                <button onClick={() => handleLanguageSelect('English')}>English</button>
            </div>
        </div>
    );
};

export default LanguageSelection;
