import React, { useState } from 'react';
import axios from 'axios';
import styles from './KuralSearch.module.css';
import { franc } from 'franc';
// Import the franc library

const KuralSearch = () => {
    const [selectedField, setSelectedField] = useState('');
    const [inputValue, setInputValue] = useState('');
    const [results, setResults] = useState([]);
    const [error, setError] = useState('');

    const detectLanguage = (text) => {
        const langCode = franc(text);
        return langCode === 'tam' ? 'tamil' : 'english'; // You can add more conditions for other languages if needed
    };

    const handleSearch = async () => {
        console.log('Input Value:', inputValue);
        
        // Detect language based on input value
        const language = detectLanguage(inputValue);
        console.log('Detected Language:', language);

        // Set selectedField based on detected language
       

        if(selectedField==='chapterName'){
            if (language === 'tamil') {
                setSelectedField('chapterName'); // Assuming default field for Tamil
            } else {
                setSelectedField('Chapter_Eng'); // Assuming default field for English
            }
        }
        else if(selectedField==='sectionName'){
            if (language === 'tamil') {
                setSelectedField('sectionName'); // Assuming default field for Tamil
            } else {
                setSelectedField('section_eng'); // Assuming default field for English
            }
        }
    else if(selectedField==='verse')

        try {
            const response = await axios.get('http://localhost:5000/api/kurals', {
                params: {
                    [selectedField]: inputValue
                }
            });
            
            setResults(response.data);
            setError('');
        } catch (err) {
            setError('Error fetching data');
            setResults([]);
        }
    };

    const handleFieldSelect = (field) => {
        setSelectedField(field);
        setInputValue(''); // Reset input when field changes
    };

    return (
        <div className={styles.container}>
            <h1>Search Thirukkural</h1>
            
            <div className={styles.buttonContainer}>
                <button onClick={() => handleFieldSelect('chapterName')}>Chapter Name</button>
                <button onClick={() => handleFieldSelect('sectionName')}>Section Name</button>
                <button onClick={() => handleFieldSelect('verse')}>Verse</button>
            </div>

            {selectedField && (
                <div>
                    <label>{selectedField.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}:</label>
                    <input 
                        type={selectedField === 'number' ? 'number' : 'text'} 
                        value={inputValue} 
                        onChange={(e) => setInputValue(e.target.value)} 
                    />
                </div>
            )}
            
            <button style={{margin:'20px auto'}} onClick={handleSearch}>Search</button>

            {error && <p className={styles.error}>{error}</p>}

            <ul>
                {results.map((kural, index) => (
                    <li key={index}>
                        <p>Chapter Name: {kural.chapterName}</p>
                        <p>Section Name: {kural.sectionName}</p>
                        <p>Verse: {kural.verse}</p>
                        <p>{kural.translation}</p>
                        <p>{kural.explanation}</p>
                        <p>Number: {kural.number}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default KuralSearch;
