import React, { useState } from 'react';
import axios from 'axios';
import styles from './KuralSearch.module.css';

const KuralSearch = () => {
    const [selectedField, setSelectedField] = useState('');
    const [inputValue, setInputValue] = useState('');
    const [results, setResults] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const handleSearch = async () => {
       
        setLoading(true);
        
        const timeout = new Promise((_, reject) => 
            setTimeout(() => reject(new Error('Request timed out')), 15000) // Increased to 15 seconds
        );

        try {
            const response = await Promise.race([
                axios.get('http://localhost:5000/api/kurals', {
                    params: {
                        [selectedField]: inputValue
                    }
                }),
                timeout
            ]);
            setResults(response.data);
            setError('');
        } catch (err) {
            console.error('Error fetching data:', err);
            setError('Error fetching data. Please try again.');
            setResults([]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className={styles.container}>
            <h1>Search Thirukkural</h1>
            
            <div>
                <label>Select Field:</label>
                <select value={selectedField} onChange={(e) => setSelectedField(e.target.value)}>
                    <option value="">Select a field</option>
                    <option value="chapterName">Chapter Name</option>
                    <option value="sectionName">Section Name</option>
                    <option value="verse">Verse</option>
                </select>
            </div>

            {selectedField && (
                <div>
                    <label>{selectedField.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}:</label>
                    <input 
                        type={selectedField === 'number' ? 'number' : 'text'} 
                        value={inputValue} 
                        onChange={(e) => setInputValue(e.target.value)} 
                    />
                </div>
            )}
            
            <button onClick={handleSearch} disabled={loading}>
                {loading ? 'Searching...' : 'Search'}
            </button>

            {error && <div className={styles.error}>{error}</div>}
            {results.length > 0 && (
                <div>
                    <h2>Results:</h2>
                    <ul>
                        {results.map((kural, index) => (
                            <li key={index}>{`${kural.verse} (${kural.chapterName})`}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default KuralSearch;
